}

async function getVideos(model, req, res) {
  const videoRelations = await model.findMany({
    where: {
      userId: req.user.id
    },
    orderBy: {
      createdAt: 'desc'
    }
  });
  const VideoIds = videoRelations.map(videoLike => videoLike.videoId);
  let videos = await prisma.video.findMany({
    where: {
      id: {
        in: VideoIds
      }
    },
    include: {
      user: true
    }
  });
  console.log("Befor if check", videos);

  if (!videos.length) {
    return res.status(200).json({
      videos
    });
  }

  videos = await (0, _video.getVideoViews)(videos);
  res.status(200).json({
    videos
  });
}

async function toggleSubscribe(req, res, next) {
  if (req.user.id === req.params.userId) {
    return next({
      message: "You can`t subscribe to your own channel",
      statusCode: 400
    });
  }

  const user = await prisma.user.findUnique({
    where: {
      id: req.params.userId
    }
  });

  if (!user) {
    return next({
      message: "User with this Id doesn`t excist",
      statusCode: 404
    });
  }

  const isSubscribed = await prisma.subscription.findFirst({
    where: {
      subscriberId: {
        equals: req.user.id
      },
      subscribedToId: {
        equals: req.params.userId
      }
    }
  });

  if (isSubscribed) {
    await prisma.subscription.delete({
      where: {
        id: isSubscribed.id
      }
    });
  } else {
    await prisma.subscription.create({
      data: {
        subscriber: {
          connect: {
            id: req.user.id
          }
        },
        subscribedTo: {
          connect: {
            id: req.params.userId
          }
        }
      }
    });
  }

  res.status(200).json({});
}

async function getFeed(req, res) {
  const subscribedTo = await prisma.subscription.findMany({
    where: {
      subscriberId: {
        equals: req.user.id
      }
    }
  });
  const subscribtions = subscribedTo.map(sub => sub.subscribedToId);
  const feed = await prisma.video.findMany({
    where: {
      userId: {
        in: subscribtions
      }
    },
    include: {
      user: true
    },
    orderBy: {
      createdAt: "desc"
    }
  });

  if (!feed.length) {
    console.log("inside");
    return res.status(200).json({
      feed
    });
  }

  const feedVideos = await (0, _video.getVideoViews)(feed);
  console.log("outside");
  res.status(200).json({
    feed: feedVideos
  });
}

async function searchUser(req, res, next) {
  if (!req.query.query) {
    return next({
      message: "Please enter search query",
      statusCode: 400
    });
  }

  const users = await prisma.user.findMany({
    where: {
      username: {
        contains: req.query.query,
        mode: "insensitive"
      }
    }
  });

  if (!users.length) {
    return res.status(200).json({
      users
    });
  }

  for (const user of users) {
    const subscribersCount = await prisma.subscription.count({
      where: {
        subscribedToId: {
          equals: user.id
        }
      }
    });
    const videoCount = await prisma.video.count({
      where: {
        userId: user.id
      }
    });
    let isMe = false;
    let isSubscribed = false;

    if (req.user) {
      isMe = req.user.id === user.id;
      isSubscribed = await prisma.subscription.findFirst({
        where: {
          AND: {
            subscriberId: {
              equals: req.user.id
            },
            subscribedToId: {
              equals: user.id
            }
          }
        }
      });
    }

    user.subscribersCount = subscribersCount;
    user.videoCount = videoCount;
    user.isSubscribed = Boolean(isSubscribed);
    user.isMe = isMe;
  }

  res.status(200).json({
    users
  });
}

async function getRecommendedChannels(req, res) {
  const channels = await prisma.user.findMany({
    where: {
      id: {
        not: req.user.id
      }
    },
    take: 10
  });

  if (!channels.length) {
    return res.status(200).json({
      channels
    });
  }

  for (const channel of channels) {
    const subscribersCount = await prisma.subscription.count({
      where: {
        subscribedToId: {
          equals: channel.id
        }
      }
    });
    const videoCount = await prisma.video.count({
      where: {
        userId: channel.id
      }
    });
    const isSubscribed = await prisma.subscription.findFirst({
      where: {
        AND: {
          subscriberId: {
            equals: req.user.id
          },
          subscribedToId: {
            equals: channel.id
          }
        }
      }
    });
    channel.subscribersCount = subscribersCount;
    channel.videoCount = videoCount;
    channel.isSubscribed = Boolean(isSubscribed);
  }

  return res.status(200).json({
    channels
  });
}

async function getProfile(req, res, next) {
  const user = await prisma.user.findUnique({
    where: {
      id: req.params.userId
    }
  });

  if (!user) {
    return next({
