      }
    },
    orderBy: {
      createdAt: "desc"
    }
  })

  user.subscribtionCount = subscribersCount;
  user.isSubscribed = Boolean(isSubscribed);
  user.isMe = isMe;
  user.channels = channels;
  user.videos = videos;

  if (!videos.length) {
    return res.status(200).json({ user })
  }

  user.videos = await getVideoViews(videos)

  res.status(200).json({ user })
}

